mapper.select:
SELECT COUNT(*)
FROM tbl_cart
WHERE product_idx = #{product_idx} AND or_member_id = #{or_member_id}

mapper.update:
UPDATE tbl_cart SET
product_cnt = product_cnt + #{product_cnt}
WHERE product_idx = #{product_idx} AND or_member_id = #{or_member_id}

update tbl_cart set
product_cnt = #{product_count}
where cart_idx = #{cart_idx} and or_member_id = #{or_member_id}

mapper.select:
SELECT pay_idx, pay_method, pay_price, pay_date, pay_cancel_date, member_id, name, phone_num, email, receiver_name, receiver_phone_num, receiver_addr, cart_idx, product_idx, product_name, product_cnt
FROM tbl_payment
WHERE cart_idx = #{cart_idx}
